export default {
  SET_PRODUCTS(state, { data }) {
    state.ProductsAPI = data;
  },

  SET_PRODUCTS_BY_CATEGORY(state, { categoryId }) {
    state.Products = state.ProductsAPI.filter(
      (item) => item.category == categoryId
    );
  },
};
